    public int B_Sewa, Lama_Inap;
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int Lama_Inap, Sub_Total, Diskon, Diskon2, Total, stot;
        
        if (jComboBox1.getSelectedIndex() == 1){
            B_Sewa = 1000000;          
        }
        else if (jComboBox1.getSelectedIndex() == 2){
            B_Sewa = 1500000;
        
        }
        else if (jComboBox1.getSelectedIndex() == 3){
            B_Sewa = 2000000;
            
        }
        else if (jComboBox1.getSelectedIndex() == 4){
            B_Sewa = 2500000;
           
        }
        else if (jComboBox1.getSelectedIndex() == 5){
            B_Sewa = 3000000;           
        }
        else if (jComboBox1.getSelectedIndex() == 6){
            B_Sewa = 3500000;      
            
        }
        else if (jComboBox1.getSelectedIndex() == 7){
            B_Sewa = 4000000;                
            
        }
        Lama_Inap = Integer.parseInt(jTextField3.getText()); 
        
        Sub_Total = B_Sewa * Lama_Inap;
        
        jTextField6.setText("Rp. "+Sub_Total);
            
        Diskon = Integer.parseInt(jTextField7.getText());
        
        stot = Sub_Total * Diskon / 100;
                
        Total = Sub_Total - stot;
            
        jTextField4.setText("Rp. "+Total);      
        
    }//GEN-LAST:event_jButton1ActionPerformed
    
