    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        String Nama_Pengunjung, Alamat, Total;
        Object Tipe_Kamar, No_Kamar;
        
        Nama_Pengunjung = jTextField1.getText();
        Alamat = jTextField2.getText();
        Tipe_Kamar = jComboBox1.getSelectedItem();
        No_Kamar = jComboBox2.getSelectedItem();
        Total = jTextField4.getText();
        
        
        JOptionPane.showMessageDialog(
                null,"Cetak Sewa Kamar Hotel \n Nama Pengunjung............:  "+Nama_Pengunjung+
                     "\n Alamat.....................:  "+Alamat+
                     "\n Tipe Kamar.................:  "+Tipe_Kamar+
                     "\n Nomor Kamar................:  "+No_Kamar+
                     "\n Total Biaya................:  "+Total
        );
    }//GEN-LAST:event_jButton3ActionPerformed

